import React,{useState} from 'react';
import {Header,Divider,Container, Image, Button,Icon,Modal} from 'semantic-ui-react';

import axios from 'axios';

import Loader from '../components/Loader';
import Message from '../components/Message';
import {useParams } from 'react-router-dom';
import {api} from '../api';
import {useFetch} from '../helpers';



const DeleteModal = ({title,thumbnail}) => {
    const [error, setError] = useState(null)
    const [loading, setLoading] = useState(false)

    function handleSubmit() {
       
        setLoading(true);

        const formData = new FormData()
        formData.append("thumbnail", thumbnail)
        formData.append("title", title)
        formData.append("content", markdown)
        axios
            .post(api.posts.create, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                    "Authorization": "Token a8df07ad37824b4c5fd9ac5d4bc13e424fa4fd8b"
                }
            })
            .then(res => {
                setLoading(false);
                history.push('/')
            })
            .catch(err => {
                setLoading(false);
                setError(err.message || err)
            })
    }

    const [open,toggle] = useState(false);
    
        return (
        <Modal
            trigger={<Button onClick={() =>toggle(true)} secondary floated="right">Delete Post</Button>}
            open={open}
            onClose={() =>toggle(false)}
            size='small'
        >
        <Modal.Header>Delete this Post</Modal.Header>
            <Modal.Content image>
            <Image wrapped size='medium' src={thumbnail} />
            <Modal.Description>
                <Header>{title}</Header>
                <p>
                Are you sure, you want to delete this post?
                </p>
            </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button color='black' onClick={() =>toggle(false)}>
                    No
                </Button>
                <Button
                    positive
                    icon='checkmark'
                    labelPosition='right'
                    content="Confirm Delete"
                    onClick={handleSubmit}
                    loading ={loading}
                    disabled ={loading}
                />
          </Modal.Actions>
        </Modal>
        )
    }



const PostDetail = () =>{
    
    const {postSlug} = useParams()
    const {data,loading,error} = useFetch(api.posts.retrieve(postSlug))
    
    
    return(
        <Container text>
        {error && <Message negative message={error} />}
        {loading && <Loader/>}
        {data && (
            <div>
            <Image src={data.thumbnail}/>
            <Header as="h1">
            {data.title}
            </Header>
            <small> Last Updated: {`${new Date(data.last_updated).toLocaleDateString()}`}</small>
                <p>
                    {data.content}
                </p>
            <Divider/>
            <DeleteModal title = {data.title} thumbnail = {data.thumbnail}/>
            </div>
        )}
        
        </Container>
    )
}

export default PostDetail;
