import React,{useState} from 'react';
import {Header,Divider,Container, Image, Button,Icon,Modal} from 'semantic-ui-react';



import Loader from '../components/Loader';
import Message from '../components/Message';
import {useParams } from 'react-router-dom';
import {api} from '../api';
import {useFetch} from '../helpers';



const DeleteModal = ({title,thumbnail}) => {
    const [open,toggle] = useState(false);
    
        return (
        <Modal
            trigger={<Button onClick={() =>toggle(true)} secondary floated="right">Delete</Button>}
            open={open}
            onClose={() =>toggle(false)}
            basic
            size='small'
        >
        <Modal.Header>Delete this Post</Modal.Header>
            <Modal.Content image>
            <Image wrapped size='medium' src={thumbnail} />
            <Modal.Description>
                <Header>Delete {title}</Header>
                <p>
                Are you sure, you want to delete this post?
                </p>
            </Modal.Description>
            <Modal.Actions>
                <Button color='black' onClick={() =>toggle(false)}>
                    No
                </Button>
                <Button
                    positive
                    icon='checkmark'
                    labelPosition='right'
                    content="Confirm Delete"
                    onClick={() =>null}
                />
          </Modal.Actions>
            </Modal.Content>
        </Modal>
        )
    }



const PostDetail = () =>{
    
    const {postSlug} = useParams()
    const {data,loading,error} = useFetch(api.posts.retrieve(postSlug))
    
    
    return(
        <Container text>
        {error && <Message negative message={error} />}
        {loading && <Loader/>}
        {data && (
            <div>
            <Image src={data.thumbnail}/>
            <Header as="h1">
            {data.title}
            </Header>
            <small> Last Updated: {`${new Date(data.last_updated).toLocaleDateString()}`}</small>
                <p>
                    {data.content}
                </p>
            <Divider/>
            <DeleteModal/>
            </div>
        )}
        
        </Container>
    )
}

export default PostDetail;
