import React from 'react';
import {Header, Button, Form } from 'semantic-ui-react';

const PostCreate = () =>{
    return(
        <div>
        <Header>Create a Post</Header>
        <Form>
            <Form.Field>
                <label>Title</label>
                <input placeholder='Title of Blog' />
            </Form.Field>
            <Form.TextArea label='Content' placeholder='This is your post content' />
            <Form.Field>
                <Button content='Choose a Thumbnail' labelPosition="left" icon='file'/>
            </Form.Field>
            <Button type='submit'>Submit</Button>
        </Form>
        </div>
    )
}




export default PostCreate;
